INSERT INTO products_product VALUES(1,'System Professional Color Save Shampoo','250 ml - Detergente salva-colore, che prolunga e protegge la vivacità del pigmento.',27,'static/System_Professional_Color_Save_Shampoo.png','System Professional');
INSERT INTO products_product VALUES(2,'System Professional Color Save Conditioner','200 ml - Scorrevolezza e protezione quotidiana per i capelli colorati.',33,'static/System_Professional_Color_Save_Conditioner.png','System Professional');
INSERT INTO products_product VALUES(3,'System Professional LuxeOil Elisir','100 ml - Elisir oleoso adatto per tutti i tipi di capelli. Protettivo della cheratina che ripristina all''istante una morbidezza lievissima e uno splendore ultrabrillante.',39,'static/System_Professional_LuxeOil_Elisir.png','System Professional');
INSERT INTO products_product VALUES(4,'Sun Care Keratin Complex 10 in 1','150 ml - Un unico prodotto che assicura 10 benefici istantanei: idrata anche i capelli più secchi e danneggiati, condiziona senza appesantire, districa anche i capelli più trattati, annulla l''effetto crespo elimina le doppie punte, dona corpo e volume, nutre e ripara le fibre capillari, facilita il brushing e dona lucentezza.',26,'static/Sun_Care_Keratin_Complex_10_in_1.png','Sun Care');
INSERT INTO products_product VALUES(5,'Sun Care Maschera Ristrutturante Istantanea','200 ml - La sua duplice azione consente di agire sulle zone più fragili della cute, grazie ai Liposomi di vitamina, mentre l''apporto dei preziosi estratti di Bacche di Goji sono l''ideale per dare nuovo vigore a tutta la capigliatura che apparirà nuovamente rigenerata.',23,'static/Sun_Care_Maschera_Ristrutturante_Istantanea_wO1wsXf.png','Sun Care');
INSERT INTO products_product VALUES(6,'Sun Care Shampoo Nutriente','250 ml - Il mix di Oli di Mandorle, Semi di Lino e Borragine attivati enzimaticamente, liberano un fitocomplesso che aumenta l''elasticità del capello e ne potenzia la resistenza all''ossidazione causata da sole, salsedine e cloro. Aumenta inoltre l''idratazione del cuoio capelluto.',24,'static/Sun_Care_Shampoo_Nutriente.png','Sun Care');
INSERT INTO products_product VALUES(7,'Wella Ultimate Repair Shampoo','250 ml - Shampoo dalla consistenza lussuosa, rimuove le impurità e inizia a ricostruire i capelli ad ogni lavaggio.',22,'static/Wella_Ultimate_Repair_Shampoo.png','Wella');
INSERT INTO products_product VALUES(8,'Wella Ultimate Repair Conditioner','200 ml. - Conditioner dalla consistenza cremosa. Dona morbidezza ad ogni lavaggio, idratazione intensa e forza dall''interno.',25,'static/Wella_Ultimate_Repair_Conditioner.png','Wella');
INSERT INTO products_product VALUES(9,'Wella Ultimate Repair Protective Leave-in','140 ml - Leave In Cream controlla il crespo e aiuta lo styling. Protegge contro rottura, raggi UV, calore (230°), danni meccanici, umidità.',29,'static/Wella_Ultimate_Repair_Protective_Leave-in.png','Wella');
